/** @format */

import {memo, useEffect, useState} from 'react'
import {useHistory, useLocation} from "react-router-dom";
import {Route, Routes} from 'react-router-dom';
import axios from "axios";
import {GoBackButton} from "../../components/GoBackButton";
import {Movie, MovieNavigation, Cast, } from "../../components";

export const MovieDetailsPage = memo(() => {
  const [movie, setMovie] = useState(null);
  const [isLoading, setLoading] =useState(false);

  const history = useHistory();
  const location = useLocation();

  useEffect(() => {
    getData()
  }, [])

  const getData = () => {
    setLoading(true);
    const {movieId} = location.params.id;

    axios.get(movieId)
      .then((response) => {
        setMovie(response.data.movieId)
      })
      .catch(console.error)
      .finally(() => setLoading(false))
  };

  const handleGoBack = () => {
    history.push(location)
  };

  return(
    <>
      <GoBackButton onBack={handleGoBack}/>
      {movie && <Movie movie={movie}/>}
      {movie && <MovieNavigation/>}

      <Routes>
        <Route exact path={`${match.path}${routes.cast}`} component={Cast} />
        }
      </Routes>
    </>
  )
} )
